-- Column Level Security
ALTER TABLE <schema_name>.DimHighlyCompensatedOfficer
ALTER COLUMN hco_name ADD MASKED WITH (FUNCTION = 'default()');

ALTER TABLE <schema_name>.DimRecipient
ALTER COLUMN recipient_name ADD MASKED WITH (FUNCTION = 'default()');

GRANT UNMASK ON <database_name> TO <database_admin_placeholder>;
GRANT SELECT ON SCHEMA::<schema_name> TO <database_admin_placeholder>;

GRANT SELECT ON SCHEMA::<schema_name> TO <database_users_placeholder>;

-- Row Level Security
-- Function to return table where AgencyCode matches AgencyName
CREATE FUNCTION Security.tvf_securitypredicate(@AgencyCode AS nvarchar(50))
    RETURNS TABLE
WITH SCHEMABINDING
AS
    RETURN SELECT 1 AS tvf_securitypredicate_result
WHERE @AgencyCode = (SELECT DimAgency.agency_code FROM TestData.DimAgency WHERE agency_name = USER_NAME())OR USER_NAME() = 'DBAdmin';
GO

-- Security Policy to filter table based on agency code
-- AgencyCode must reference column name on each table
CREATE SECURITY POLICY AgencyFilter
ADD FILTER PREDICATE Security.tvf_securitypredicate(AgencyCode)
ON <database_name.table>
ADD FILTER PREDICATE Security.tvf_securitypredicate(AgencyCode)
ON <database_name.table2>
WITH (STATE = ON);
GO

-- Security Schema
CREATE SCHEMA Security;
GO

-- Grant Statements
GRANT SELECT ON Security.tvf_securitypredicate TO DBAdmin;
GRANT SELECT ON Security.tvf_securitypredicate TO Agency1;

DROP SECURITY POLICY AgencyFilter;
DROP FUNCTION Security.tvf_securitypredicate;

-- Tests
CREATE USER Agency1 WITHOUT LOGIN;

EXECUTE AS USER = 'DBAdmin';
SELECT * FROM TestData.FactTransaction;
REVERT;

EXECUTE AS USER = 'Agency1';
SELECT * FROM TestData.DimAgency;
REVERT;

EXECUTE AS USER = 'Agency1';
SELECT * FROM TestData.FactTransaction;
REVERT;
