content.query:
DROP USER [data engineer];
DROP USER [Department of Commerce];
DROP USER [Gulf Coast Ecosystem Restoration Council]

CREATE USER [data engineer] WITHOUT LOGIN;
-- CREATE USER [Department of Commerce] WITHOUT LOGIN;
CREATE USER [Gulf Coast Ecosystem Restoration Council] WITHOUT LOGIN;

-- Column Level Security
ALTER TABLE Prod.DimHighlyCompensatedOfficer
ALTER COLUMN hco_name ADD MASKED WITH (FUNCTION = 'default()');

ALTER TABLE Prod.DimRecipient
ALTER COLUMN recipient_name_raw ADD MASKED WITH (FUNCTION = 'default()');


GRANT UNMASK ON SCHEMA::Prod TO [data engineer];
GRANT SELECT ON SCHEMA::Prod TO [data engineer];

GRANT SELECT ON SCHEMA::Prod TO [Department of Commerce];
GRANT SELECT ON SCHEMA::Prod TO [Gulf Coast Ecosystem Restoration Council];

-- Security Schema
CREATE SCHEMA Security;
GO
-- Function to return table where AgencyCode matches AgencyName
CREATE FUNCTION Security.tvf_securitypredicate(@AgencyCode AS nvarchar(50))
    RETURNS TABLE
WITH SCHEMABINDING
AS
    RETURN SELECT 1 AS tvf_securitypredicate_result
WHERE @AgencyCode = (SELECT DimAgency.agency_code FROM Prod.DimAgency WHERE agency_name = USER_NAME()) OR USER_NAME() = 'data engineer';
GO

-- Security Policy to filter table based on agency code
-- AgencyCode must reference column name on each table
CREATE SECURITY POLICY AgencyFilter
-- ADD FILTER PREDICATE Security.tvf_securitypredicate(agency_code)
-- ON Prod.DimAgency,
-- ADD FILTER PREDICATE Security.tvf_securitypredicate(awarding_agency_code)
-- ON Prod.FactTransaction,
ADD FILTER PREDICATE Security.tvf_securitypredicate(funding_agency_code)
ON Prod.ViewAgenciesByDay
WITH (STATE = ON);
GO

DROP SECURITY POLICY AgencyFilter;
DROP FUNCTION Security.tvf_securitypredicate;

-- Grant Statements
GRANT SELECT ON Security.tvf_securitypredicate TO [data engineer];
GRANT SELECT ON Security.tvf_securitypredicate TO [Gulf Coast Ecosystem Restoration Council];

EXECUTE AS USER = 'data engineer';
SELECT * FROM Prod.DimHighlyCompensatedOfficer;
REVERT;

EXECUTE AS USER = 'data engineer';
SELECT * FROM Prod.DimRecipient;
REVERT;

EXECUTE AS USER = 'Gulf Coast Ecosystem Restoration Council';
SELECT * FROM Prod.DimHighlyCompensatedOfficer;
REVERT;

EXECUTE AS USER = 'Gulf Coast Ecosystem Restoration Council';
SELECT * FROM Prod.DimRecipient;
REVERT;

EXECUTE AS USER = 'data engineer';
SELECT * FROM Prod.DimAgency;
REVERT;

EXECUTE AS USER = 'Gulf Coast Ecosystem Restoration Council';
SELECT * FROM Prod.DimAgency;
REVERT;

EXECUTE AS USER = 'Gulf Coast Ecosystem Restoration Council';
SELECT * FROM Prod.FactTransaction;
REVERT;
