DROP USER [data engineer];
DROP USER [Department of Commerce];
DROP USER [Gulf Coast Ecosystem Restoration Council]

CREATE USER [data engineer] WITHOUT LOGIN;
CREATE USER [Department of Commerce] WITHOUT LOGIN;
CREATE USER [Gulf Coast Ecosystem Restoration Council] WITHOUT LOGIN;

-- Column Level Security
ALTER TABLE Prod.DimHighlyCompensatedOfficer
ALTER COLUMN hco_name ADD MASKED WITH (FUNCTION = 'default()');

ALTER TABLE Prod.DimRecipient
ALTER COLUMN recipient_name_raw ADD MASKED WITH (FUNCTION = 'default()');


GRANT UNMASK ON SCHEMA::Prod TO [data engineer];
GRANT SELECT ON SCHEMA::Prod TO [data engineer];

GRANT SELECT ON SCHEMA::Prod TO [Department of Commerce];
GRANT SELECT ON SCHEMA::Prod TO [Gulf Coast Ecosystem Restoration Council];

EXECUTE AS USER = 'data engineer';
SELECT * FROM Prod.DimHighlyCompensatedOfficer;
REVERT;

EXECUTE AS USER = 'Department of Commerce';
SELECT * FROM Prod.DimHighlyCompensatedOfficer;
REVERT;

EXECUTE AS USER = 'data engineer';
SELECT * FROM Prod.DimAgency;
REVERT;

-- Issue here with duplicate agencies without leading 0s
EXECUTE AS USER = 'Department of Commerce';
SELECT * FROM Prod.DimAgency;
REVERT;

EXECUTE AS USER = 'Gulf Coast Ecosystem Restoration Council';
SELECT * FROM Prod.DimAgency;
REVERT;

EXECUTE AS USER = 'Gulf Coast Ecosystem Restoration Council';
SELECT * FROM Prod.FactTransaction;
REVERT;
